class Solution {
    public int longestBeautifulSubstring(String word) {
        char[] chr = {'a', 'e', 'i', 'o', 'u'};
        int left = 0, right = 0, index = 0, max = 0;
        while (right < word.length()) {
            while (right < word.length() && word.charAt(right) != chr[0]) {
                right++;
            }
            left = right;

            while (right < word.length() && word.charAt(right) == chr[index]) {
                right++;
                if (right < word.length() && index + 1 < 5 && word.charAt(right) == chr[index + 1]) index++;
            }

            if (index == 4) {
                max = Math.max(right - left, max);
            }

            index = 0;
            left = right;
        }
        return max;
    }
}

public class LongestSubstringOfAllVowelsinOrder {
    public static void main(String[] args) {
        String word = "";
        Solution solution = new Solution();
        System.out.println(solution.longestBeautifulSubstring(word));
    }
}

